INSERT INTO db_blog_ricette.ricette(number_portions, image, preparation_time, title, ingredients, `text`) VALUES(4, 'https://i.pinimg.com/originals/f0/09/4f/f0094f9a77932ddb7cedf9c418530f26.png', '30 minuti', 'Rigatoni salsiccia e broccoli', '500g broccoli, 350g rigatoni, 2 salsiccie, aglio, peperoncino e olio', 'I rigatoni salsiccia e broccoli sono un primo piatto veloce da preparare, gustoso e nutriente. Un piatto di pasta adatto a tutta la famiglia!');
INSERT INTO db_blog_ricette.ricette(number_portions, image, preparation_time, title, ingredients, `text`) VALUES(2, 'https://statics.cucchiaio.it/content/cucchiaio/it/ricette/2009/12/ricetta-pollo-cacciatora/jcr:content/header-par/image_single.img10.jpg/1429534872166.jpg', '55 minuti', 'Pollo alla cacciatora', '600g pollo a pezzi, 500g cipolle, 500g carote, 500g coste di sedano, 500g aglio, 200g pomodori pelati, 50g olive nere', 'Un secondo piatto classico della cucina popolare, ricco e gustoso e perfetto per il pranzo della domenica in famiglia.');
INSERT INTO db_blog_ricette.ricette(number_portions, image, preparation_time, title, ingredients, `text`) VALUES(8, 'https://www.fattoincasadabenedetta.it/wp-content/uploads/2023/01/SFOGLIA_RUSTICA_ALLA_DIAVOLA_SITO-2.jpg', '35 minuti', 'Sfoglia rustica alla diavola', '100g salame piccante, 100g provola, 120g passata di pomodoro, 50g olive nere, origano, peperoncino', 'Una torta salata piccante e gustosa, facilissima da fare e perfetta come aperitivo, come antipasto o come piatto unico per pranzi e cene veloci.');
